--COWORKER --+--------------------------------------------------------------------------------------------------------------------- --+ Сотрудники --+---------------------------------------------------------------------------------------------------------------------
--+--------------------------------------------------------------------------------------------------------------------- --+ Создаем миграционную таблицу migration.coworker_teacher; --+--------------------------------------------------------------------------------------------------------------------- create table if not exists sdb.coworker_teacher( aid bigint primary key, first_name character varying, second_name character varying, middle_name character varying, organization_id bigint, phone character varying, email character varying );
--+--------------------------------------------------------------------------------------------------------------------- --+ Создаем миграционную таблицу migration.coworker_user --+--------------------------------------------------------------------------------------------------------------------- create table if not exists sdb.coworker_user( aid bigint primary key, name character varying, organization_id bigint, position character varying, user_id character varying, phone character varying, email character varying );
--+--------------------------------------------------------------------------------------------------------------------- --+ Создание перекодировочной таблицы migration.coworker_map --+--------------------------------------------------------------------------------------------------------------------- create table if not exists sdb.coworker_map( id bigint, aid bigint primary key );
--+--------------------------------------------------------------------------------------------------------------------- --+ Создаем для предварительного анализа данных таблицу migration.coworkers (аналогичную основной) --+--------------------------------------------------------------------------------------------------------------------- create table if not exists sdb.coworkers( id bigint primary key, first_name varchar(255), second_name varchar(255), middle_name varchar(255), organization_id bigint, manager_id bigint, position varchar(800), fired_date date, is_teacher boolean, is_deputy boolean, is_director boolean, is_coordinator boolean, user_id bigint );

--ORGANIZATION --+--------------------------------------------------------------------------------------------------------------------- --+ Организации --+---------------------------------------------------------------------------------------------------------------------
--+--------------------------------------------------------------------------------------------------------------------- --+ Создание таблицы migration.organization --+--------------------------------------------------------------------------------------------------------------------- create table if not exists sdb.organization( aid bigint primary key, short_title character varying, full_title character varying, inn varchar(12), kpp varchar(9), ogrn varchar(20), parent_organization_id bigint, representative_full_name varchar(200), representative_position varchar(200), website varchar(500), email varchar(500), phone character varying, description character varying, unom bigint, full_address character varying, opf_id int, is_provider int, is_css_organization int, is_dspp_organization int, types_providing_services_id int, territory_code bigint -- получаем из TerritoryEntity по адресу te.Id = adr.TerritoryEntityId );
--+--------------------------------------------------------------------------------------------------------------------- --+ migration.recurrence_schedule --+--------------------------------------------------------------------------------------------------------------------- create table if not exists sdb.recurrence_schedule( aid bigint primary key, -- оригинальный идентификатор организации day_of_week int, time_from varchar(32), time_to varchar(32) );
--+--------------------------------------------------------------------------------------------------------------------- --+ migration.organization_map перекодировочная таблица организаций --+--------------------------------------------------------------------------------------------------------------------- create table if not exists sdb.organization_map( id bigint primary key, aid bigint ); create index if not exists indx_organiztion_map_oid on sdb.organization_map(aid);

--PLACE --+--------------------------------------------------------------------------------------------------------------------- --+ Площадки --+---------------------------------------------------------------------------------------------------------------------
create table if not exists sdb.place ( id bigint primary key, address_id bigint not null, title varchar(4096), address varchar(800) not null, unom varchar(20) );
create table if not exists sdb.map_place ( id_map bigint primary key, id_main bigint not null, id bigint );

--PARTICIPANT --+--------------------------------------------------------------------------------------------------------------------- --+ Личное дело - Участники --+---------------------------------------------------------------------------------------------------------------------
--+--------------------------------------------------------------------------------------------------------------------- --+ создание migration.participant (Личное дело - Участники) --+--------------------------------------------------------------------------------------------------------------------- create table if not exists sdb.participant( aid bigint primary key, status_id int, first_name character varying, second_name character varying, patronymic character varying, date_of_birth character varying, gender int, home_phone_number character varying, personal_phone_number character varying, email character varying, snils character varying, skm character varying, skm_series character varying, reg_full_address character varying, fct_full_address character varying, document_type_id bigint, serial_number character varying, date_from character varying, department character varying, department_code character varying );
--+--------------------------------------------------------------------------------------------------------------------- --+ создание перекодировочной таблицы участников migration.participant_map --+--------------------------------------------------------------------------------------------------------------------- create table if not exists sdb.participant_map( id bigint, aid bigint primary key );

--BASELINE
--+---------------------------------------------------------------------------------------------------------------------
--+ Миграция из ЕСЗ 1.0
--+---------------------------------------------------------------------------------------------------------------------

DROP SCHEMA IF EXISTS sdb CASCADE;
CREATE SCHEMA sdb;

DROP SCHEMA IF EXISTS idb CASCADE;
CREATE SCHEMA idb;

--+---------------------------------------------------------------------------------------------------------------------
--+ Функция определения идентификатора в нашей схеме по идентификатору из ЕСЗ
--+---------------------------------------------------------------------------------------------------------------------
create or replace function sdb.get_organization_id_by_esz_id(in bigint) returns bigint as
$$
declare result bigint;
begin
    select id into  result from sdb.organization_map where aid = $1;
    if result isnull then
        select nextval('md.contact_owner_id_seq') into result;
        insert into md.contact_owner(id, created, modified) values (result, now(), now());
        insert into sdb.organization_map(id, oid) values (result, $1);
    end if;
    return result;
end;
$$ language plpgsql;

--+---------------------------------------------------------------------------------------------------------------------
--+ Функция получения id ЕСЗ 2.0 по id ЕСЗ 1.0 и id ЕСЗ 1.0 справочника направления. Результатом является id ЕСЗ 2.0
--+ профиля активности и создание записи в md.participant_activity_profile
--+---------------------------------------------------------------------------------------------------------------------
create or replace function sdb.get_participant_activity_profile_id(in aid bigint, in act_id bigint) returns bigint as
$$
declare
    result bigint;
    mid bigint;
begin
    select m.id into result from sdb.participant_activity_profile_map m where m.aid = aid and m.activity_id = act_id;
    if result isnull then
        select nextval('md.participant_activity_profile_id_seq') into result;
        select id into mid from sdb.ref_activity_map m where m.aid = act_id;
--      TODO подумать
        insert into md.participant_activity_profile(id, activity_id) values (result, act_id);
        insert into sdb.participant_activity_profile_map(id, aid, activity_id) values (result, aid, mid);
    end if;
    return result;
end;
$$ language plpgsql;

--+---------------------------------------------------------------------------------------------------------------------
--+ Функция использует перекодировочную таблицу для справочника Направления. Если нет данных в перекодировочной таблице
--+ то берется следующее id из основной последовательности справочника Направления (reference.activity_id_seq) и вносим
--+ информацию в перекодировочную таблицу
--+---------------------------------------------------------------------------------------------------------------------
create or replace function sdb.get_map_activity_id(in orig_id bigint) returns bigint as
$$
declare result bigint;
begin
    if orig_id isnull then
        return null;
    end if;
    select id into  result from sdb.ref_activity_map where aid = orig_id;
    if result isnull then
        select nextval('reference.activity_id_seq') into result;
        insert into sdb.ref_activity_map(id, aid) values (result, orig_id);
    end if;
    return result;
end;
$$ language plpgsql;

--+---------------------------------------------------------------------------------------------------------------------
--+ Функция получения id ЕСЗ 2.0 по id ЕСЗ 1.0 участника
--+---------------------------------------------------------------------------------------------------------------------
create or replace function sdb.get_participant_by_ec3(in bigint) returns bigint as
$$
declare
    result bigint;
begin
    select id into result from sdb.participant_map where aid = $1;
    return result;
end
$$ language plpgsql;

--+---------------------------------------------------------------------------------------------------------------------
--+ Функция получения id ЕСЗ 2.0 через перекодировочную таблицу по id ЕСЗ 1.0 для сотрудников
--+---------------------------------------------------------------------------------------------------------------------
create or replace function sdb.get_coworkers_id_by_map_id(in bigint) returns bigint as
$$
declare result bigint;
begin
    select id into result from sdb.coworkers_map where aid=$1;
    if result isnull then
        select nextval('md.coworkers_id_seq') into result;
        insert into md.contact_owner(id, created, modified) values (result, now(), now());
        insert into sdb.coworkers_map(id, aid) values (result, $1);
    end if;
    return result;
end;
$$ language plpgsql;


--+---------------------------------------------------------------------------------------------------------------------
--+ Функция определения идентификатора адреса из ЕХД по УНОМу
--+---------------------------------------------------------------------------------------------------------------------
create or replace function idb.get_address_id_by_unom(in bigint) returns bigint as
$$
declare result bigint;
begin
    select (select max(arr) into result from unnest(array (select id from ar.address_registry where unom = $1)) arr);
    return result;
end;
$$ language plpgsql;


--+---------------------------------------------------------------------------------------------------------------------
--+ Функция соответствия Время проведения занятий
--+---------------------------------------------------------------------------------------------------------------------
create or replace function idb.get_daytime(in bigint) returns bigint as
$$
begin
    case
        when $1 = 1 then return 2;
        when $1 = 2 then return 3;
        when $1 = 4 then return 4;
        else return null;
    end case;
end;
$$ language plpgsql;

--+---------------------------------------------------------------------------------------------------------------------
--+ Функция количество дней недели по типу дней недели для профиля активности
--+---------------------------------------------------------------------------------------------------------------------
create or replace function idb.get_profile_active_weekdays_by_typeid(in bigint) returns bigint array as
$$
begin
    case
        when $1 = 0 then return '{0,1,2,3,4}';
        when $1 = 1 then return '{6,7}';
        else return null;
    end case ;
end;
$$ language plpgsql;

--+---------------------------------------------------------------------------------------------------------------------
--+ Функция перекодировки статуса участника
--+---------------------------------------------------------------------------------------------------------------------
create or replace function idb.convert_participant_status(in status_id int, in decline_reason_id int) returns bigint as
$$
declare result bigint;
begin
    case
        when status_id = 1 then select id into result from reference.participant_status where title='started_classes';
        when status_id = 2 or status_id = 3 then select id into result from reference.participant_status where title='group_selection';
        when status_id = 4 or status_id = 6 then select id into result from reference.participant_status where title='direction_selection';
        when status_id = 5 then select id into result from reference.participant_status where title='crerated';
        when status_id = 8 then select id into result from reference.participant_status where title='rejection';
        when status_id = 7 and decline_reason_id = 4 then select id into result from reference.participant_status where title='self_rejection';
        when status_id = 7 then select id into result from reference.participant_status where title='rejection';
        else result := null;
    end case;
    return result;
end
$$ language plpgsql;

--+---------------------------------------------------------------------------------------------------------------------
--+ Функция перекодировки для справочника типов документов
--+---------------------------------------------------------------------------------------------------------------------
create or replace function idb.get_document_type_by_EC3_id(in bigint) returns bigint as
$$
begin
    case
        when $1 = 1 then return 7;
        when $1 = 2 then return 1;
        when $1 = 5 then return 3;
        when $1 = 6 then return 4;
        when $1 = 9 then return 2;
        else return null;
    end case;
end;
$$ language plpgsql;

--+---------------------------------------------------------------------------------------------------------------------
--+ Функция добавления адреса в адресном реестре
--+---------------------------------------------------------------------------------------------------------------------
create or replace function idb.add_full_address(in character varying) returns bigint as
$$
declare result bigint;
begin
    select id into result from ar.address_registry where address = $1;
    if result isnull then
        select nextval('ar.address_registry_id_seq') into result;
        insert into ar.address_registry(id, address) values (result, $1);
    end if;
    return result;
end;
$$ language plpgsql;

import org.apache.nifi.controller.ControllerService
import groovy.sql.Sql

def flowFile = session.get()
if (flowFile == null) {
    return
}

def lookup = context.controllerServiceLookup
def dbServiceName = dbPoolName.value
def sqlStatement = SQLStatement.evaluateAttributeExpressions(flowFile).value
def dbcpServiceId = lookup.getControllerServiceIdentifiers(ControllerService).find {
    cs -> lookup.getControllerServiceName(cs) == dbServiceName
}
//def dbcpServiceId = dbPoolId.value

def conn = lookup.getControllerService(dbcpServiceId)?.getConnection()
try {
    flowFile = session.write(flowFile, {outputStream ->
        def sql = new Sql(conn)

        sql.execute(sqlStatement)

    } as OutputStreamCallback)

    session.transfer(flowFile, REL_SUCCESS)
} catch(e) {
    log.error('Scripting error', e)
    session.transfer(flowFile, REL_FAILURE)
}

conn?.close()

