select Id as aid, FirstName first_name, LastName as second_name, MiddleName as middle_name, OrganizationId as organization_id,
       Phone as phone, Email as email
from esz.Teacher where IsArchive is null or IsArchive = 0;

drop function if exists idb.get_participant_activity_profile_id;
create or replace function idb.get_participant_activity_profile_id(in ec3_aid bigint, in act_id bigint) returns bigint as
$$
declare
    result bigint;
    mid bigint;
begin
	if ec3_aid isnull or act_id isnull then
		return null;
	end if;
    select m.id into result from idb.participant_activity_profile_map m where m.aid = ec3_aid and m.activity_id = act_id;
    if result isnull then
        select nextval('md.participant_activity_profile_id_seq') into result;
        insert into idb.participant_activity_profile_map(id, aid, activity_id) values (result, ec3_aid, act_id);
    end if;
    return result;
end;
$$ language plpgsql;

truncate table idb.ref_activity;
truncate table idb.participant_activity_profile;
truncate table idb.participant_activity_profile_preferred_daytime;
truncate table idb.participant_activity_profile_weekday;

--+---------------------------------------------------------------------------------------------------------------------
--+ перенос данных для справочника Направления (reference.activity)
--+---------------------------------------------------------------------------------------------------------------------
insert into idb.ref_activity(id, title, parent_id)
select
    idb.get_map_activity_id(ta.aid),
    ta.title,
    idb.get_map_activity_id(ta.parent_id)
from sdb.ref_activity ta left join idb.ref_activity_map m on m.aid = ta.aid order by ta.parent_id
on conflict (id) do update set title = excluded.title, parent_id = excluded.parent_id;

--+---------------------------------------------------------------------------------------------------------------------
--+ вносит данные в idb.participant_activity_profile, idb.participant_activity_profile_preferred_daytime,
--+ idb.participant_activity_profile_weekday
--+---------------------------------------------------------------------------------------------------------------------
do $$
declare
    cur cursor for select * from sdb.participant_activity_profile;
    paid bigint;
    pid bigint;
    daytimeId bigint;
    indx bigint;
    days bigint array;
    cl bigint;
    classificators int array[3];
begin
    for row in cur loop
        pid := idb.get_participant_by_ec3(row.participant_id);

        if pid is not null then
            classificators := array[row.first_classificator, row.second_classificator, row.third_classificator];
            foreach cl in array classificators loop
                if cl is not null then

                    paid := idb.get_participant_activity_profile_id(row.aid, cl);

                    if paid is not null then
                        insert into idb.participant_activity_profile(
                            id,
                            activity_id,
                            participant_id,
                            date_from,
                            comment,
                            status_id
                        ) values (
                            paid,
                            idb.get_map_activity_id(cl),
                            pid,
                            to_date(row.date_from, 'DD.MM.YYYY'),
                            row.comment,
                            1
                        ) on conflict do nothing;

                       	daytimeId := idb.get_daytime(row.schedule_type_id);
                        if daytimeId is not null then
                            insert into idb.participant_activity_profile_preferred_daytime(activity_id, preferred_daytime_id) values (paid, daytimeId)
                           	on conflict do nothing;
                    	end if;

                        days := idb.get_profile_active_weekdays_by_typeid(row.week_days_type_id);
                        if days is not null then
                            foreach indx in array days loop
                                insert into idb.participant_activity_profile_weekday(activity_id, weekday) values (paid, indx)
                                on conflict do nothing;
                            end loop;
                    	end if;
                    end if;

                end if;
            end loop;
        end if;
    end loop;
end $$ language plpgsql;

truncate table idb.contract;
truncate table idb.contract_property;
truncate table idb.contract_status_registry;

do $$
declare
    cid bigint;
    cpid bigint;
    csrid bigint;
    contract_cur cursor for select * from sdb.contract;
begin
    for row in contract_cur loop
        select idb.get_contract_id_by_esz_id(row.aid) into cid;
        insert into idb.contract(
            id,
            provider_id,
            contract_number,
            organization_id,
            date_from,
            date_to,
            document_number
        ) values (
            cid,
            idb.get_organization_id_by_esz_id(row.provider_id),
            row.contract_number,
            idb.get_organization_id_by_esz_id(row.organization_id),
            to_timestamp(row.date_from, 'YYYY-MM-DD'),
            to_timestamp(row.date_to, 'YYYY-MM-DD'),
            nextval('idb.contract_document_number_seq')
        );

        select nextval('md.contract_property_id_seq') into cpid;
        insert into idb.contract_property(
            id,
            contract_id,
            activity_id,
            grant_value
        ) values (
            cpid,
            cid,
            idb.get_map_activity_id(row.activity_id),
            row.full_price
        );

        select nextval('md.contract_status_registry_id_seq') into csrid;
        insert into idb.contract_status_registry(
            id,
            contract_id,
            status_id,
            status_reason_id,
            start_date,
            end_date,
            idm_ent_user_profiles_id
        ) values (
            csrid,
            cid,
            5,
            1,
            to_timestamp(row.date_from, 'YYYY-MM-DD'),
            null,--to_timestamp(row.date_to, 'YYYY-MM-DD'),
            1
        );

    end loop;
end;
$$ language plpgsql;

truncate table idb.coworkers;
delete from idb.contact_owner where id in (select ow.id from idb.contact_owner ow join idb.contact c on c.owner_id=ow.id where c.contact_owner_type_id=2);
delete from idb.contact c where c.contact_owner_type_id=2;

--+---------------------------------------------------------------------------------------------------------------------
--+ трансформация из миграционной таблицы в основную
--+---------------------------------------------------------------------------------------------------------------------
do $$
declare
    pid bigint;
    cid bigint;
    arr_nm varchar array;
    usr_cur cursor for select * from sdb.coworker_user;
    tch_cur cursor for select * from sdb.coworker_teacher t join sdb.groups g on t.aid=g.coworker_id;
begin
    for row in usr_cur loop
        arr_nm := regexp_split_to_array(row.name, '\s+');
        select idb.get_coworkers_id_by_map_id(row.aid) into pid;
        insert into idb.coworkers(id, second_name, first_name, middle_name, organization_id, position, is_teacher, is_deputy, is_director, user_id)
        values (pid, arr_nm[0], arr_nm[1], arr_nm[2], idb.get_organization_id_by_esz_id(row.organization_id), row.position, 0::boolean, 0::boolean, 0::boolean, 111);

        insert into idb.contact_owner(id, created, modified) values (pid, now(), now()) on conflict do nothing;

        if row.phone is not null then
            select nextval('md.contact_id_seq') into cid;
--            insert into idb.contact_owner(id, created, modified) values (cid, now(), now()) on conflict do nothing; -- FIXME
            insert into idb.contact(id, owner_id, contact_owner_type_id, contact_type_id, value, contact_availability_type_id)
            values (cid, pid, 2, 1, row.phone, 1) on conflict do nothing; -- FIXME
        end if;
        if row.email is not null then
            select nextval('md.contact_id_seq') into cid;
--            insert into idb.contact_owner(id, created, modified) values (cid, now(), now()) on conflict do nothing; -- FIXME
            insert into idb.contact(id, owner_id, contact_owner_type_id, contact_type_id, value, contact_availability_type_id)
            values (cid, pid, 2, 2, row.email, 1) on conflict do nothing; -- FIXME
        end if;
    end loop;
    for row in tch_cur loop
        select idb.get_coworkers_id_by_map_id(row.aid) into pid;
        insert into idb.coworkers(id, second_name, first_name, middle_name, organization_id, is_teacher, is_deputy, is_director, user_id)
        values (pid, row.second_name, row.first_name, row.middle_name, idb.get_organization_id_by_esz_id(row.organization_id), 1::boolean, 0::boolean, 0::boolean, 111)
        on conflict do nothing; -- FIXME

        insert into idb.contact_owner(id, created, modified) values (pid, now(), now()) on conflict do nothing;

        if row.phone is not null then
            select nextval('md.contact_id_seq') into cid;
--            insert into idb.contact_owner(id, created, modified) values (cid, now(), now()) on conflict do nothing; -- FIXME
            insert into idb.contact(id, owner_id, contact_owner_type_id, contact_type_id, value, contact_availability_type_id)
            values (cid, pid, 2, 1, row.phone, 1) on conflict do nothing; -- FIXME
        end if;
        if row.email is not null then
            select nextval('md.contact_id_seq') into cid;
--            insert into idb.contact_owner(id, created, modified) values (cid, now(), now()) on conflict do nothing; -- FIXME
            insert into idb.contact(id, owner_id, contact_owner_type_id, contact_type_id, value, contact_availability_type_id)
            values (cid, pid, 2, 2, row.email, 1) on conflict do nothing; -- FIXME
        end if;
    end loop;
end;
$$ language plpgsql;

truncate table idb.groups; truncate table idb.group_status_registry; --truncate table idb.schedule; --truncate table idb.week_day_schedule; truncate table idb.class_record; truncate table idb.class_record_status_registry;
-- вспомогательная таблица маппинг id групп create table if not exists idb.groups_map ( aid bigint NOT NULL, id bigint NOT NULL, CONSTRAINT "groups_map_aid" UNIQUE ("aid"), CONSTRAINT "groups_map_id" UNIQUE ("id") );
-- вспомогательныфе функции -- маппинг sdb.groups.aid на наш внутренний md.groups.id create or replace function idb.get_group_id_by_map_aid(i_aid bigint) RETURNS bigint AS $body$ DECLARE gr_id bigint; BEGIN select id into gr_id from idb.groups_map where aid = i_aid; if gr_id isnull then select nextval('md.groups_id_seq') into gr_id; insert into idb.groups_map(id, aid) values (gr_id, i_aid); end if; return gr_id; END $body$ language plpgsql;
-- день недели из числа -- не соответствует описанию в wiki (воскресенье - 7) create or replace function idb.get_DOW(day_in int) returns varchar as $body$ begin case when day_in = 0 then return 'SUNDAY'; when day_in = 1 then return 'MONDAY'; when day_in = 2 then return 'TUESDAY'; when day_in = 3 then return 'WEDNESDAY'; when day_in = 4 then return 'THURSDAY'; when day_in = 5 then return 'FRIDAY'; when day_in = 6 then return 'SATURDAY'; else return null; end case; end; $body$ language plpgsql;
create or replace function idb.get_class_record_status(status_1 int, sc_status int) returns bigint as $body$ declare res int; begin case when status_1 = 1 then res := 1; -- FIXME return (1, 3, 6) в зависимости от $2 when status_1 = 2 or status_1 = 3 then res := 5; -- статус Отчислен else res := 1; end case; return res; end; $body$ language plpgsql;
create or replace function idb.get_group_status(i_aid int, sc_status int) returns bigint as $body$ declare res int; begin case when i_aid = 3 then res := 8; when i_aid = 2 then res := 7; when i_aid = 1 then res := 3; when i_aid isnull then res := 1; when sc_status = 3 then res := 14; end case; return res; end; $body$ language plpgsql;
-- процесс ----------
do $$ DECLARE i_group_id bigint; i_schedule_id bigint; i_place_id bigint; i_class_record_id bigint; act_profile_id bigint; row record;
BEGIN for row in select * from sdb.groups loop
i_group_id := idb.get_group_id_by_map_aid(row.aid);
insert into idb.groups( id, need_note, min_count, max_count, -- fact_count, plan_start_date, plan_end_date, fact_start_date, fact_end_date, extend, organization_id, activity_id, -- comment coworker_id, -- sync, contract_id, territory_centre_id ) values ( i_group_id, row.need_note::boolean, row.min_count, row.max_count, to_date(row.plan_start_date, 'YYYY-MM-DD'), to_date(row.plan_end_date, 'YYYY-MM-DD'), to_date(row.fact_start_date, 'YYYY-MM-DD'), to_date(row.fact_end_date, 'YYYY-MM-DD'), row.extend::boolean, idb.get_organization_id_by_esz_id(row.organization_id), (case when idb.get_map_activity_id(row.activity_id) is null then 1 else idb.get_map_activity_id(row.activity_id) end), idb.get_coworkers_id_by_map_id(row.coworker_id), idb.get_contract_id_by_esz_id(row.contract_id), idb.get_organization_id_by_esz_id(row.territory_centre_id) );
-- вставляем в реестр текущее значение статуса insert into idb.group_status_registry( id, group_id, status_id, start_date, is_expectation ) values ( nextval('md.group_status_registry_id_seq'), i_group_id, idb.get_group_status(row.group_status_id, row.sc_status_id), -- не уверен насчёт точного соответствия, возможно нужен тоже маппинг now(), -- либо с планируемой/фактической даты начала false );
end loop;
-- for row in select * from sdb.schedule -- loop -- -- i_schedule_id := nextval('md.schedule_id_seq'); -- просто получаем из последовательности -- i_group_id := idb.get_group_id_by_map_aid(row.group_id); -- select id into i_place_id from idb.map_place where id_main = row.place_id; -- todo get_place? -- -- insert into idb.schedule( -- id, -- group_id, -- place_id, -- start_time, -- end_time, -- start_date, -- end_date -- -- pause -- ) values ( -- i_schedule_id, -- idb.get_group_id_by_map_aid(row.group_id), -- i_place_id, -- to_timestamp(row.start_time, 'HH24:MI:SS')::time, -- to_timestamp(row.end_time, 'HH24:MI:SS')::time, -- to_date(row.start_date, 'YYYY-MM-DD'), -- to_date(row.end_date, 'YYYY-MM-DD') -- ); -- -- insert into idb.week_day_schedule( -- id, -- schedule_id, -- place_id, -- start_time, -- end_tim -- -- e, -- start_date, -- end_date, -- -- pause, -- day_of_week -- ) values ( -- nextval('md.week_day_schedule_id_seq'), -- i_schedule_id, -- i_place_id, -- to_timestamp(row.start_time, 'HH24:MI:SS')::time, -- to_timestamp(row.end_time, 'HH24:MI:SS')::time, -- to_date(row.start_date, 'YYYY-MM-DD'), -- to_date(row.end_date, 'YYYY-MM-DD'), -- idb.get_DOW(row.day_of_week) -- ); -- end loop;
for row in select distinct on (participant_id, group_id) cr.status_id as status_id, cr.reques_ad_id as reques_ad_id, g.activity_id as activity_id, g.group_status_id as group_status_id, to_timestamp(cr.date_from, 'YYYY-MM-DD') as date_from, to_timestamp(cr.date_to, 'YYYY-MM-DD') as date_to, pm.id as participant_id, gm.id as group_id, (case when cr.status_id = 3 then true else false end) as transferred, max(papm.id) over (partition by papm.aid) as act_profile_id from sdb.class_record cr left join sdb.groups g on (cr.group_id = g.aid) join idb.groups_map gm on (cr.group_id = gm.aid) join idb.participant_map pm on (cr.participant_id = pm.aid) join idb.participant_activity_profile_map papm on (cr.reques_ad_id=papm.aid)
loop i_class_record_id := nextval('md.class_record_id_seq'); --act_profile_id := idb.get_participant_activity_profile_id(row.reques_ad_id, row.activity_id);
--if act_profile_id is not null then insert into idb.class_record( id, participant_id, group_id, -- assigned_at participant_activity_profile_id, date_from, date_to, -- pause_date_from -- pause_date_to transferred ) values ( i_class_record_id, row.participant_id,--idb.get_participant_by_ec3(row.participant_id), row.group_id,--idb.get_group_id_by_map_aid(row.group_id), row.act_profile_id,--idb.get_participant_activity_profile_id(row.reques_ad_id, row.activity_id), -- TODO доработать хранимку для случая null row.date_from, row.date_to, row.transferred );
insert into idb.class_record_status_registry( id, class_record_id, class_record_status_id, -- comment, -- communication_history_id, start_date, end_date -- reason ) values ( nextval('md.class_record_status_registry_id_seq'), i_class_record_id, idb.get_class_record_status(row.status_id, row.group_status_id), row.date_from, row.date_to ); --end if; end loop; END $$ language plpgsql;
-- постпроцессинг статусов update idb.group_status_registry set status_id = 1;
update idb.group_status_registry set status_id = 13 where group_id in ( select id from idb.groups where plan_end_date < now() order by id );
update idb.group_status_registry set status_id = 8 where group_id in ( select id from idb.groups where now() between plan_start_date and plan_end_date order by id );
update idb.group_status_registry set status_id = 3 where group_id in ( select id from idb.groups where now() < plan_start_date and now() < fact_start_date order by id );
-- в статусе черновик остаются группы, у которых планируемая дата начала больше текущей даты (2019-10-09), -- а фактическая дата начала меньше текущей даты (2019-01-12).
-- проверка статусов: -- select status_id, count(*) -- from idb.group_status_registry -- group by status_id -- order by status_id;

truncate table idb.ref_department;
truncate table idb.organization;
truncate table idb.ref_recurrence_schedule;
delete from idb.contact c where c.contact_owner_type_id=4;
delete from idb.contact_owner where id in (select ow.id from idb.contact_owner ow join idb.contact c on c.owner_id=ow.id where c.contact_owner_type_id=4);

--+---------------------------------------------------------------------------------------------------------------------
--+ вносит данные в idb.organization, idb.contact, idb.ref_recurrence_schedule и при необходимости в ar.address_registry
--+---------------------------------------------------------------------------------------------------------------------
drop function if exists idb.is_valid_docnum;
create or replace function idb.is_valid_docnum(textd varchar) returns boolean language plpgsql immutable as $$
begin
  return case when textd::bigint is null then false else true end;
exception when others then
  return false;
end;$$;

--+---------------------------------------------------------------------------------------------------------------------
--+ Переносим данные в целевую таблицу ref_department
--+---------------------------------------------------------------------------------------------------------------------
insert into idb.ref_department(id, title, long_title, level, key)
select
    idb.get_department_id_by_vedomstvo_id(v.aid),
    v.title,
    v.long_title,
    v.level::boolean,
    (case when v.level = 1 then 'D_SOZ' else concat('ESZ', idb.get_department_id_by_vedomstvo_id(v.aid)::varchar) end)
from sdb.vedomstvo v
on conflict (id) do update set title=excluded.title, long_title=excluded.long_title, level=excluded.level, key=excluded.key;

do $$
    declare
        cur cursor for
            select
                o.aid as aid,
                idb.get_organization_id_by_esz_id(o.aid) as id,
                o.short_title, o.full_title,
                (case when idb.is_valid_docnum(replace(o.inn,'-','')) then cast(replace(o.inn,'-','') as bigint) else 111 end) as inn,
                (case when idb.is_valid_docnum(replace(o.kpp,'-','')) then cast(replace(o.kpp,'-','') as bigint) else 111 end) as kpp,
                (case when idb.is_valid_docnum(replace(o.ogrn,'-','')) then cast(replace(o.ogrn,'-','') as bigint) else 111 end) as ogrn,
                idb.get_organization_id_by_esz_id(o.parent_organization_id) as parent_organization_id,
                idb.get_department_id_by_vedomstvo_id(o.department_id) as department_id,
                o.representative_full_name,
                o.representative_position, o.website, o.email, o.phone, o.description, o.unom, o.full_address,
                o.opf_id as opf_id,
                (case when o.is_provider = 1 then 1 else 0 end) as is_provider,
                (case when o.is_css_organization = 1 and o.types_providing_services_id = 3 then  3 when o.is_dspp_organization = 1 then 2 else null end) as level_id,
                (select max(t.id) from ar.territory t where t.ar_code = cast(o.territory_code as varchar)) as territory_id,
                (case when o.is_css_organization=1 then case when o.types_providing_services_id=3 then 0 when o.types_providing_services_id=4 then 1 else null end else null end) as is_filial
            from sdb.organization o order by o.aid;
        legalAddress bigint;
        dayOfWeek int;
        timeFrom time;
        timeTo time;
    begin
        for row in cur loop
            select
            	(case when s.time_from isnull then null else to_timestamp(s.time_from, 'HH24:MI:SS')::time end),
                (case when s.time_to isnull then null else to_timestamp(s.time_to, 'HH24:MI:SS')::time end),
                s.day_of_week
            into timeFrom, timeTo, dayOfWeek
            from sdb.ref_recurrence_schedule s where s.oid = row.aid;

            insert into idb.contact_owner(id, created, modified) values (row.id, now(), now()) on conflict do nothing;

            legalAddress := idb.get_address_id_by_unom(row.unom);

            if legalAddress isnull then
                insert into ar.address_registry(address, ar_object_status_id) values (row.full_address, 2) returning id into legalAddress;
            end if;
            insert into idb.organization(
                id, short_title, full_title, inn, kpp, ogrn, parent_organization_id, representative_full_name,
                representative_position, website, description, legal_address, physical_address, opf_id, is_provider, level_id,
                --territory_id,
                is_filial, department_id
            ) values (
                row.id,
                row.short_title,
                substring(row.full_title, 0, 800),
                row.inn,
                row.kpp,
                row.ogrn,
                row.parent_organization_id,
                substring(row.representative_full_name, 0, 800),
                substring(row.representative_position, 0, 800),
                row.website,
                substring(row.description, 0, 800),
                legalAddress,
                legalAddress, -- такое же значение пишется в физ. адрес
                row.opf_id,
                (case when row.is_provider = 1 then true else false end),
                row.level_id,
                --row.territory_id,
                (case when row.is_filial = 1 then true else false end),
                row.department_id
            );

            if dayOfWeek = 8 then
                for i in 0..4 loop
                    insert into idb.ref_recurrence_schedule(id, organization_id, day_of_week, time_from, time_to)
                    values (nextval('reference.recurrence_schedule_id_seq'), row.id, i, timeFrom, timeTo);
                end loop;
            elseif dayOfWeek = 9 then
                for i in 5..6 loop
                    insert into idb.ref_recurrence_schedule(id, organization_id, day_of_week, time_from, time_to)
                    values (nextval('reference.recurrence_schedule_id_seq'), row.id, i, timeFrom, timeTo);
                end loop;
            elseif dayOfWeek is not null then
                insert into idb.ref_recurrence_schedule(id, organization_id, day_of_week, time_from, time_to)
                values (nextval('reference.recurrence_schedule_id_seq'), row.id, dayOfWeek, timeFrom, timeTo);
            end if;

            if row.phone is not null then
                insert into idb.contact(id, owner_id, contact_owner_type_id, contact_type_id, value, contact_availability_type_id)
                values (nextval('md.contact_id_seq'), row.id, 4, 1, row.phone, 1);
            end if;
            if row.email is not null then
                insert into idb.contact(id, owner_id, contact_owner_type_id, contact_type_id, value, contact_availability_type_id)
                values (nextval('md.contact_id_seq'), row.id, 4, 2, row.email, 1);
            end if;
        end loop;
--        close cur;
--        commit work;
    end
$$ language plpgsql;

