-- добавляем индекс для полнотекстового поиска по регистру адресов
CREATE INDEX if not exists "idx_address" ON idb.ar_address_registry ("address");


-- вспомогательная функция
-- маппинг sdb.participant.aid на наш внутренний md.participant.id
create or replace function idb.participant_aid_to_id(
	i_aid bigint
)
RETURNS void AS
$body$
DECLARE
    pt_id bigint;
BEGIN
    select nextval('md.contact_owner_id_seq') into pt_id;
    insert into idb.contact_owner(id, created, modified) values (pt_id, now(), now());
    insert into idb.participant_map(id, aid) values (pt_id, i_aid);
END
$body$
language plpgsql;


-- вспомогательная функция для заполнения адресного регистра
-- рег. и факт. адрес -> id
-- возвращает id добавленной записи
create or replace function idb.get_address_id(
	i_address varchar
)
RETURNS bigint AS
$body$
DECLARE
	address_id bigint;
BEGIN
    select id into address_id from idb.ar_address_registry where address=i_address;

    if address_id is null then
        select nextval('ar.address_registry_id_seq') into address_id;
        insert into idb.ar_address_registry(id, address, ar_object_status_id, p1)
        	values (address_id, i_address, 2, 10);
    end if;

    return address_id;
END
$body$
language plpgsql;


-- Функция перекодировки статуса участника. только без выборок на каждый статус
create or replace function idb.convert_participant_status_simple(
	status_id         int,
	decline_reason_id int
)
RETURNS bigint AS
$body$
DECLARE
	result bigint;
BEGIN
	case
		when status_id = 1 then select 7 into result;
		when status_id = 2 or status_id = 3 then select 6 into result;
		when status_id = 4 or status_id = 6 then select 4 into result;
		when status_id = 5 then select 3 into result;
		when status_id = 7 and decline_reason_id = 4 then select 8 into result;
		when status_id = 8 or status_id = 7 then select 1 into result;

		else select 3 into result;
	end case;

	return result;
END
$body$
language plpgsql;


--+-----------------------------------------------------------------------------
--+ Функция перекодировки для справочника типов документов, без изменений
--+-----------------------------------------------------------------------------
create or replace function idb.get_document_type_by_EC3_id(type_id bigint) returns bigint as
$$
begin
    case
        when type_id = 1 then return 7;
        when type_id = 2 then return 1;
        when type_id = 5 then return 3;
        when type_id = 6 then return 4;
        when type_id = 9 then return 2;
        else return null;
    end case;
end;
$$ language plpgsql;


-- процесс
----------
-- заполняем participant_map и contact_owner
do $$
declare
	res bigint;
	cur cursor for (select * from sdb.participant s left join idb.participant_map m on s.aid=m.aid where m.aid is null);
begin
	for row in cur loop
    	perform idb.participant_aid_to_id(row.aid);
    end loop;
end;
$$ language plpgsql;

-- заполняем address_map и ar_address_registry
drop table if exists idb.address_map;
select
	aid,
	idb.get_address_id(reg_full_address) reg_id,
	idb.get_address_id(fct_full_address) fact_id
into idb.address_map
from sdb.participant; -- exp ~0.6s, без индекса 4.6s
-- 205,462 rows affected. (14.578 s)


-- наконец, заполняем idb.participant, idb.contact, idb.personal_document, status_log
do $$
declare
    pid  bigint;
    arr  varchar array[2];
    indx varchar;
    row  record;
begin
    for row in select * from sdb.participant
    	inner join idb.participant_map using (aid)
    	left join idb.address_map using (aid)
    loop

        insert into idb.participant(
            id,
            --status_id,
            first_name,
            second_name,
            patronymic,
            date_of_birth,
            gender,
            snils,
            skm,
            skm_series,
            registration_address,
            fact_address,
            agreement
        )
        values (
            row.id,
            row.first_name,
            row.second_name,
            row.patronymic,
            to_date(row.date_of_birth, 'YYYY-MM-DD'),
            row.gender,
            row.snils,
            row.skm,
            row.skm_series,
            row.reg_id,
            row.fact_id,
            true
        );

        insert into idb.contact_owner(id, created, modified) values (row.id, now(), now()) on conflict do nothing;

		-- вставка доп. информации

		-- телефоны
		arr := array[row.home_phone_number, row.personal_phone_number];        
        foreach indx in array arr 
        loop
            if indx is not null then
                insert into idb.contact
                values (nextval('md.contact_id_seq'), row.id, 1, 1, indx, null, 1);
            end if;
        end loop;

        -- email
        if row.email is not null then
            insert into idb.contact
            values (nextval('md.contact_id_seq'), row.id, 1, 2, row.email, null, 1);
        end if;

        -- статусы
        insert into idb.ref_participant_status_log(
        	id,
        	participant_id, 
        	status_id, 
        	start_date
    	)
        values (
        	nextval('reference.participant_status_log_id_seq'), 
        	row.id,
        	idb.convert_participant_status_simple(row.status_id, 3),
        	now()
    	);

        insert into idb.personal_document
        values (
        	nextval('md.personal_document_id_seq'),
        	row.id,
        	idb.get_document_type_by_EC3_id(row.document_type_id),
        	row.serial_number,
            to_date(row.date_from, 'YYYY-MM-DD'),
           	row.department,
          	row.department_code,
          	null
        );

    end loop;
end;
$$ language plpgsql;

truncate table idb.place_metro_stations; truncate table idb.place;
-- truncate table idb.map_place;
do $$ begin
insert into idb.map_place(id, id_map, id_main) select nextval('md.place_id_seq'), unnest(arr) as id_map, id_main from ( select (select max(idmn) from unnest(arr) idmn) id_main, arr from (select array_agg(distinct id order by id desc) arr from sdb.place group by address_id, title) data ) xx on conflict do NOTHING;
insert into idb.place( id, title, address, validation ) select distinct u.id, mp.title, idb.get_address_id_by_unom_address(mp.unom, mp.address), true from idb.map_place u join sdb.place mp on mp.id = u.id_main on conflict do nothing;
-- связь с метро insert into idb.place_metro_stations select nextval('md.place_metro_stations_id_seq'), mp.id, ms.id from sdb.place u join ar.metro_station ms on ms.aid = u.metro_station_code join idb.map_place mp on mp.id_map=u.id;
--PARAM.MIGRATION.SQL.DUBLICATE with upd_dublicate as ( select mp1.id_map as id_map, mp2.id as id from idb.map_place mp1 join idb.map_place mp2 on mp1.id_main = mp2.id_main where mp1.id is null and mp2.id is not null ) update idb.map_place set id = (select id from upd_dublicate where upd_dublicate.id_map = map_place.id_map) where id is null; end; $$ language plpgsql;

create or replace function idb.get_role(role_aid bigint) returns int as
$body$
begin
	case
		when role_aid = 217 then return 44;
		when role_aid = 221 then return 45;
		when role_aid = 225 then return 46;
		when role_aid = 227 then return 47;
		when role_aid = 230 then return 48;
		when role_aid = 233 then return 49;
	else return null;
	end case;
end;
$body$ language plpgsql;

--+---------------------------------------------------------------------------------------------------------------------
--+ Переносим данные в целевую таблицу ent_users
--+---------------------------------------------------------------------------------------------------------------------
insert into idb.ent_users(id, lastname, firstname, middlename, email, organizationid, creation_date, phone, locale, last_login)
select
    idb.get_user_id_by_ec3_id(iu.id),
    iu.lastname,
    iu.firstname,
    iu.middlename,
    iu.email,
    1,
    to_timestamp((iu.creation_date::bigint)/1000),
    iu.phone,
    'en-US',
    to_timestamp((iu.last_login::bigint)/1000)
from sdb.ent_users iu
on conflict (id) do update set lastname=excluded.lastname, firstname=excluded.firstname, middlename=excluded.middlename, email=excluded.email,
    organizationid=excluded.organizationid, creation_date=excluded.creation_date, phone=excluded.phone, locale=excluded.locale, last_login=excluded.last_login;

--+---------------------------------------------------------------------------------------------------------------------
--+ Переносим данные в две целевые таблицы: idb.ent_user_profiles, idb.ent_local_user_profiles - из idb.ent_user_profile
--+---------------------------------------------------------------------------------------------------------------------
do $$
declare
    prf_id bigint;
    cur cursor for (select
   		userid,
   		login,
   		password,
   		to_timestamp((password_last_change_date::bigint)/1000) as password_last_change_date
   	from sdb.ent_user_profile);
begin
    for row in cur loop
        select nextval('idb.ent_user_profiles_id_seq') into prf_id;
        insert into idb.ent_user_profiles(id, userid, login, resourceid) values (prf_id, idb.get_user_id_by_ec3_id(row.userid), row.login, 1);
        insert into idb.ent_user_profiles_map(user_aid, profile_id) values (row.userid, prf_id);
        insert into idb.ent_local_user_profiles(id, password, password_last_change_date)
       	values (
       		prf_id,
       		row.password,
       		(case when row.password_last_change_date is not null then row.password_last_change_date else now() end)
       	);
    end loop;
end;
$$ language plpgsql;

--+---------------------------------------------------------------------------------------------------------------------
--+ Переносим данные в idb.ent_user_lock
--+---------------------------------------------------------------------------------------------------------------------
insert into idb.ent_user_lock(id, user_id, lock_from, type)
select
	nextval('idb.ent_user_lock_id_seq'),
	idb.get_user_id_by_ec3_id(user_id),
	(case when to_timestamp((lock_from::bigint)/1000) is not null then to_timestamp((lock_from::bigint)/1000) else now() end),
	'AUTH_MAX_ATTEMPT'
from sdb.ent_user_lock;


insert into idb.dep_user_profiles_roles(
    id,
    entityid,
    roleid,
    datefrom
) select
    nextval('idb.dep_user_profiles_roles_id_seq'),
    m.profile_id,
    idb.get_role(d.role_id),
    now()
from sdb.dep_user_profiles_roles d
join idb.ent_user_profiles_map m on d.aid=m.user_aid;

