import org.apache.nifi.controller.ControllerService
import groovy.sql.Sql

def flowFile = session.get()
if (flowFile == null) {
    return
}

def lookup = context.controllerServiceLookup
def dbServiceName = dbPoolName.value
def sqlStatement = SQLStatement.evaluateAttributeExpressions(flowFile).value
//def dbcpServiceId = lookup.getControllerServiceIdentifiers(ControllerService).find {
//    cs -> lookup.getControllerServiceName(cs) == dbServiceName
//}
def dbcpServiceId = dbPoolId.value

def conn = lookup.getControllerService(dbcpServiceId)?.getConnection()
try {
    flowFile = session.write(flowFile, {outputStream ->
        def sql = new Sql(conn)

        sql.execute(sqlStatement)

    } as OutputStreamCallback)

    session.transfer(flowFile, REL_SUCCESS)
} catch(e) {
    log.error('Scripting error', e)
    session.transfer(flowFile, REL_FAILURE)
}

conn?.close()

ALTER SEQUNCE if exists idb.ent_users_id_seq RESTART WITH 100001;
create sequence if not exists idb.ent_users_id_seq start 100001;

ALTER SEQUENCE if exists idb.ent_user_profiles_id_seq RESTART WITH 100001;
create sequence if not exists idb.ent_user_profiles_id_seq start 100001;

ALTER SEQUENCE if exists idb.ent_user_lock_id_seq RESTART WITH 100001;
create sequence if not exists idb.ent_user_lock_id_seq start 100001;
